import React, { useState, useEffect } from 'react';
import { Route, Switch } from "react-router-dom";
import axios from "axios";
import './App.css';
import Home from '../Home/Home';
import CityData from '../CityData/CityData';
import SearchResults from '../SearchResults/SearchResults';

function App() {

  const [input, setInput] = useState("");
  const [isClicked, setClick] = useState(false);
  const [cityData, updateCityData] = useState([]);


  const changeInput = (event) => {
    setInput(event.target.value)
  }

  const handleClick = () => {
    setClick(true)
  }

  useEffect(async () => {
    const response = await axios.get("https://api.teleport.org/api/urban_areas/slug:san-francisco-bay-area/scores/");
    console.log(response);
    updateCityData(response.data.categories);
  }, [])

  return (
    <div className="App">
      <Switch>
        <Route exact path="/">
            <Home />
        </Route>
        <Route path="/citydata">
          <CityData input={input} changeInput={changeInput} isClicked={isClicked} />
        </Route>
        <Route path="/citydata/:city">
            <SearchResults cityData={cityData} updateCityData={updateCityData}/>
        </Route>
      </Switch>
    </div>
  );
}


export default App;
